import React, { Fragment, useState } from "react";

function AddCard({ OnClick }) {
  /**
 * <input
            className="form-control"
            id="name"
            type="text"
            name="name"
            onChange={handleNameChange}
            value={newDeckName}
 */
  const [newCard, setNewCard] = useState({ front: "", back: "", deckId: 0 });

  const handleFront = (event) => {
    //setNewCard();
    console.log(event.target.value);
		setNewCard({...newCard, front: `${event.target.value}`})
  };

  const handleBack = (event) => {
    console.log(event.target.value);
		setNewCard({...newCard, back: `${event.target.value}`})

  };

  return (
    <Fragment>
      <h3>Current Deck: Add Card</h3>
      <div className="mb-3">
        <label for="exampleFormControlTextarea1" className="form-label">
          Front
        </label>
        <textarea
          className="form-control"
          id="front"
          type="text"
          name="front"
          onChange={handleFront}
          //value={newCard.front}
          rows="2"
          placeholder="Front side of card"
        ></textarea>
      </div>
      <div className="mb-3">
        <label for="exampleFormControlTextarea1" className="form-label">
          Back
        </label>
        <textarea
          className="form-control"
          id="back"
          type="text"
          name="back"
          onChange={handleBack}
          //value={newCard.back}
          rows="2"
          placeholder="Back side of card"
        ></textarea>
      </div>
      <button className="btn btn-secondary" id="doneAddCard" onClick={OnClick}>
        Done
      </button>
      <button className="btn btn-primary" id="saveAddCard" onClick={OnClick}>
        Save
      </button>
    </Fragment>
  );
}

export default AddCard;
