import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { updateDeck } from "../utils/api";

function EditDeck({ OnClick, editState, setEditState }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();
  /*
  const [editState, setEditState] = useState({
    decks: { name: "", description: "" },
    placeholderName: "...loading",
    placeholderDescription: "...loading",
    editedDeck: {
      id: deckId,
      name: "",
      description: "",
    },
  });*/

  const onSubmit = (event) => {
    updateDeck(editState.editedDeck);
    history.push(`/decks/${deckId}`);
  };

  const handleNameChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, name: event.target.value },
    });
  };

  const handleDescriptionChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, description: event.target.value },
    });
  };

  useEffect(() => {
    async function loadDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const decksFromAPI = await response.json();
      setEditState({
        ...editState,
        decks: decksFromAPI,
        placeholderName: decksFromAPI[deckId - 1].name,
        placeholderDescription: decksFromAPI[deckId - 1].description,
      });
    }
    loadDecks();
  }, []);

  return (
    <Fragment>
      <div className="mb-3">
        <h1>Edit Deck</h1>
        <label htmlFor="name" className="form-label">
          Name
        </label>
        <input
          type="text"
          className="form-control"
          id="name"
          //placeholder={editState.placeholderName}
          onChange={handleNameChange}
          value={editState.editedDeck.name}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="description"
          rows="3"
          placeholder={editState.placeholderDescription}
          onChange={handleDescriptionChange}
          value={editState.editedDeck.description}
        ></textarea>
        <button
          className="btn btn-secondary"
          id="cancelEditDeck"
          onClick={OnClick}
        >
          Cancel
        </button>
        <button
          className="btn btn-primary"
          id="submitEditDeck"
          onClick={onSubmit}
        >
          Submit
        </button>
      </div>
    </Fragment>
  );
}

export default EditDeck;
