import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { readCard, readDeck, updateCard } from "../utils/api";
import NavBar from "./NavBar";

function EditCard({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const cardId = params.cardId;
  const history = useHistory();
  console.log(params);

  const [editCardState, setEditCardState] = useState({
    card: { front: "", back: "" },
  });

  useEffect(() => {
    async function loadPage() {
      const deck = await readDeck(deckId).then((deck) => {
        console.log(deck);
        setEditCardState({
          ...editCardState,
          deckName: deck.name,
          cards: deck.cards,
          id: deck.id,
        });
      });

      const card = await readCard(cardId).then((card) => {
        setEditCardState({
          ...editCardState,
          front: card.front,
          back: card.back,
          id: card.id,
          deckId: card.deckId,
        });
      });
    }
    loadPage();
  }, []);

  useEffect(() => {
    /*const card = readCard(cardId).then((card) => {
      setEditCardState({
        ...editCardState,
        front: card.front,
        back: card.back,
        id: card.id,
        deckId: card.deckId,
      });
    });*/
  }, []);

  const handleChange = (event) => {
    if (event.target.parentNode.innerText === "Front") {
      setEditCardState({ ...editCardState, front: event.target.value });
    }
    if (event.target.parentNode.innerText === "Back") {
      setEditCardState({ ...editCardState, back: event.target.value });
    }
  };

  const onCancel = () => {
    console.log(editCardState.deckName);
    history.push(`/deck/${deckId}`);
  };

  const onSubmit = () => {
    updateCard(editCardState.card);
  };

  return (
    <Fragment>
      <NavBar deckName={editCardState.deckName} />
      <h2 className="mt-2">Edit Card</h2>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Front
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.front}
          value={editCardState.front}
          onChange={handleChange}
        ></textarea>
      </div>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Back
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.back}
          value={editCardState.back}
          onChange={handleChange}
        ></textarea>
      </div>
      <button
        className="btn btn-secondary mr-2"
        id="doneEditCard"
        onClick={onCancel}
      >
        Cancel
      </button>
      <button
        className="btn btn-primary"
        id="submitEditCard"
        onClick={onSubmit}
      >
        Submit
      </button>
    </Fragment>
  );
}

export default EditCard;
