import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

function Study({ OnClick, currentDeckId, setCurrentDeckId }) {
  const params = useParams();
  const deckId = params.deckId;
  const [studyCardNum, setStudyCardNum] = useState(0);
  const [studyState, setStudyState] = useState({
    studyCardNum: 0,
    flipped: false,
  });

  const handleSetDeck = (id) => {
    setCurrentDeckId(id);
  };
  //
  const [studyDeck, setStudyDeck] = useState(false);

  //let front = "|" + "" + "|";

  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/cards?deckId=${deckId}`
        //`http://localhost:5000/cards?deckId=${currentDeckId}`
      );
      const deck = await response.json();
      console.log(deck);
      setStudyDeck(deck);
      const front = await studyDeck.front;
    }
    loadDeck();
  }, []);

  //console.log(studyDeck);

  const bothBtns = (
    <Fragment>
      <button onClick={OnClick} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={OnClick} id="flipBack" className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const front = studyDeck
    ? studyDeck[studyState.studyCardNum].front
    : "loading...";


  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Card X of Y</h5>
        <p className="card-text">
          {front}
          {/*studyDeck ? studyDeck[studyState.studyCardNum].front : "loading..."*/}
          {/*flipped ? back : front*/}
        </p>
        {/*flipped ? bothBtns : flipBtn*/}
        {bothBtns}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: Current Deck Name</h1>
      {studyCard}
    </Fragment>
  );
}

export default Study;
