import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";
import { readDeck } from "../utils/api";

function ViewDeck({ OnClick }) {
  const [currentDeck, setCurrentDeck] = useState({});
  const params = useParams();
  const deckId = params.deckId;
  //let cards = "...loading";

  useEffect(() => {
    const deck = readDeck(deckId);
    deck.then((deck) => {
      console.log(deck);
      setCurrentDeck({
        name: deck.name,
        description: deck.description,
        cards: deck.cards,
        id: deck.id,
      });
    });
  }, []);

  const deckControlButtons = (
    <Fragment>
      <button className="btn btn-secondary" id="editDeck" onClick={OnClick}>
        Edit
      </button>
      <button className="btn btn-primary" id="studyDeck" onClick={OnClick}>
        Study
      </button>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
      <button className="btn btn-danger" id="deleteDeck" onClick={OnClick}>
        Delete
      </button>
    </Fragment>
  );

  const cardTemplate = (front, back, id) => (
    <Fragment>
      <li className="list-group-item">
        <div className="row">
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{front}</p>
              </div>
            </div>
          </div>
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{back}</p>
              </div>
              <div className="text-right" id={`${id}`}>
                <button
                  className="btn btn-secondary"
                  id="editCard"
                  onClick={OnClick}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger"
                  id="deleteCard"
                  onClick={OnClick}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      </li>
    </Fragment>
  );

  const cards = currentDeck.cards
    ? currentDeck.cards.map((card) =>
        cardTemplate(card.front, card.back, card.id)
      )
    : "no";

  return (
    <Fragment>
      <h4>{currentDeck.name}</h4>
      <p>{currentDeck.description}</p>
      {deckControlButtons}
      <h2>Cards</h2>
      <ul className="list-group">{cards}</ul>
    </Fragment>
  );
}

export default ViewDeck;
