import React from "react";

function DeckForm({ OnClick, newDeckObject, setNewDeckObject }) {
  const handleChange = (event) => {
    setNewDeckObject({
      ...newDeckObject,
      [event.target.name]: event.target.value,
    });
  };



  return (
    <form>
      <div className="mb-3">
        <label htmlFor="deck-name" className="form-label">
          Name
        </label>
        <input
          className="form-control"
          id="name"
          type="text"
          name="name"
          onChange={handleChange}
          value={newDeckObject.name}
          placeholder="Deck Name"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="deck-description" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="description"
          type="password"
          name="description"
          onChange={handleChange}
          value={newDeckObject.description}
          placeholder="Brief description of the deck"
        />
      </div>
      <button
        type="button"
        className="btn btn-secondary mr-2"
        id="cancelCreateDeck"
        onClick={OnClick}
      >
        Cancel
      </button>
      <button
        type="button"
        className="btn btn-primary"
        id="submitCreateDeck"
        onClick={OnClick}
      >
        Submit
      </button>
    </form>
  );
}

export default DeckForm;
