  studyDeck,
  setStudyDeck,
  studyState,
  setStudyState,
  OnClick,
}) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  useEffect(() => {
		const deck = readDeck(deckId);



	})

  deck.then((response) => {
    setStudyDeck(response);
  });

  return (
    <Fragment>
      <h1>Study: {studyDeck ? studyDeck.name : "...loading"}</h1>
    </Fragment>
  );
