import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { readCard, readDeck, updateCard } from "../utils/api";
import BreadcrumbBar from "./BreadcrumbBar";
import CardForm from "./CardForm";

function EditCard({ newCard, setNewCard, currentDeck }) {
  const params = useParams();
  const deckId = params.deckId;
  const cardId = params.cardId;
  const history = useHistory();

  const [editCardState, setEditCardState] = useState({
    edit: false,
  });

  useEffect(() => {
    readDeck(deckId).then((deck) => {
      setEditCardState({ ...editCardState, edit: true, deckName: deck.name });
    });
    /*
    function loadDeck() {
      readDeck(deckId).then((deck) => {
        setEditCardState({
          ...editCardState,
          edit: true,
          deckId: deck.id,
          deckName: deck.name,
        });
      });
    }

    loadDeck();
    loadCard();
*/
  }, []);
  console.log(editCardState);

  /*
  function loadCard() {
    readCard(cardId).then((card) => {
      setEditCardState({
        ...editCardState,
        front: card.front,
        back: card.back,
        edit: true,
        deckName: editCardState.deckName,
      });
    });
  }*/

  const handleChange = (event) => {
    if (event.target.parentNode.innerText === "Front") {
      setEditCardState({ ...editCardState, front: event.target.value });
    }
    if (event.target.parentNode.innerText === "Back") {
      setEditCardState({ ...editCardState, back: event.target.value });
    }
  };

  const onCancel = () => {
    //console.log(editCardState.deckName);
    setEditCardState({ edit: false });
    history.push(`/decks/${deckId}`);
  };

  const onSubmit = () => {
    const editedCard = {
      front: editCardState.front,
      back: editCardState.back,
      id: cardId,
      deckId: deckId,
    };
    updateCard(editedCard);
    history.push(`/decks/${deckId}`);
    setEditCardState({ edit: false });
  };

  if (editCardState.edit) {
    return (
      <Fragment>
        <BreadcrumbBar
          nav1={`Deck ${currentDeck.name}`}
          link1={`/decks/${deckId}`}
          nav2={`Edit Card ${cardId}`}
        />
        <h2 className="mt-2">Edit Card</h2>
        <CardForm
          newCard={newCard}
          setNewCard={setNewCard}
          cardFront={editCardState.front}
          cardBack={editCardState.back}
        />
        <button
          className="btn btn-secondary mr-2"
          id="doneEditCard"
          onClick={onCancel}
        >
          Cancel
        </button>
        <button
          className="btn btn-primary"
          id="submitEditCard"
          onClick={onSubmit}
        >
          Submit
        </button>
      </Fragment>
    );
  }
  return "Loading";
}

export default EditCard;

