import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

function Study({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;

  //const [studyState.cards, setStudyDeck] = useState(false);
  const [studyState, setStudyState] = useState({
    studyCardNum: 0,
    flipped: false,
    cards: {},
  });
  //
  //const cards = studyDeck.cards;
  //console.log(cards);
  //
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}?_embed=cards`
      );
      const deck = await response.json();
      //setStudyDeck(deck.cards);
      setStudyState({ ...studyState, cards: deck.cards });
    }
    loadDeck();
  }, []);

  const onFlip = () => {
    setStudyState({ ...studyState, flipped: !studyState.flipped });
  };

  const onNext = () => {
    setStudyState({
      ...studyState,
      flipped: false,
      studyCardNum: studyState.studyCardNum + 1,
    });
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} id="flipBack" className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const front = studyState.cards
    ? studyState.cards[studyState.studyCardNum].front
    : "loading...";
  const back = studyState.cards
    ? studyState.cards[studyState.studyCardNum].back
    : "loading...";

  const deckLength = studyState.cards ? studyState.cards.length : "...loading";

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {studyState.studyCardNum + 1} of {deckLength}
        </h5>
        <p className="card-text">{studyState.flipped ? back : front}</p>
        {studyState.flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: Current Deck Name</h1>
      {studyCard}
    </Fragment>
  );
}

export default Study;
