import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

function CurrentDeck({ OnClick }) {
  const currentDeck = {
    name: "Deck name",
    description: "a brief deck description",
  };

  const params = useParams();
  const deckId = params.deckId;

  const [thisDeck, setThisDeck] = useState({});
  const [cardsArray, setCardsArray] = useState({});

  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(`http://localhost:5000/decks/${deckId}`);
      const deckFromAPI = await response.json();
      setThisDeck(deckFromAPI);
    }
    loadDeck();
  }, []);

  useEffect(() => {
    async function loadCards() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}/cards`
      );
      const cardsFromAPI = await response.json();
      setCardsArray(cardsFromAPI);
    }
    loadCards();
  }, []);

  const deckControlButtons = (
    <Fragment>
      <button className="btn btn-secondary" id="editDeck" onClick={OnClick}>
        Edit
      </button>
      <button className="btn btn-primary" id="studyDeck" onClick={OnClick}>
        Study
      </button>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
      <button className="btn btn-danger" id="deleteDeck" onClick={OnClick}>
        Delete
      </button>
    </Fragment>
  );

  const cardTemplate = (front, back) => (
    <Fragment>
      <div class="row">
        <div class="col-sm-6">
          <div class="card">
            <div class="card-body">
              <p class="card-text">{front}</p>
            </div>
          </div>
        </div>
        <div class="col-sm-6">
          <div class="card">
            <div class="card-body">
              <h5 class="card-title">Special title treatment</h5>
              <p class="card-text">
                {back}
              </p>
              <a href="#" class="btn btn-primary">
                Go somewhere
              </a>
            </div>
          </div>
        </div>
      </div>
    </Fragment>

    /*
    <Fragment>
      <div className="row">
        <div className="col-sm-6">
          <div className="card border-right-0">
            <div className="card-body">
              <p className="card-text">{front}</p>
            </div>
            <p className="card-text text-right">{back}</p>

            <div>
              <button className="btn btn-secondary">Edit</button>
              <button className="btn btn-danger">Delete</button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  */
  );

  //take cards array and save variable of card elements
  console.log(cardsArray);
  const newCardsArray = Array.from(cardsArray);

  const cardsDisplay = newCardsArray.map((card) =>
    cardTemplate(card.front, card.back)
  );

  const cards = <div className="card-group">{cardTemplate}</div>;

  return (
    <Fragment>
      <h4>{thisDeck.name}</h4>
      <p>{thisDeck.description}</p>
      {deckControlButtons}
      <h2>Cards</h2>
      {cardsDisplay}
    </Fragment>
  );
}

export default CurrentDeck;
