import React, { Fragment, useEffect, useState } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import CreateDeck from "./CreateDeck";
import DeckList from "./DeckList";
//import NewDeck from "./NewDeck";
import Study from "./Study";
import NavBar from "./NavBar";
import CurrentDeck from "./CurrentDeck";
import EditDeck from "./EditDeck";
import AddCard from "./AddCard";
import EditCard from "./EditCard";
//import TestDeck from "./TestDeck";

function Layout() {
  /*
  const deckKey = `${Math.floor(Math.random() * 10)}${Math.floor(
    Math.random() * 10
  )}${Math.floor(Math.random() * 10)}${Math.floor(Math.random() * 10)}`;
*/
  const [allDecks, setAllDecks] = useState({});
  const [newDeckName, setNewDeckName] = useState("");
  const [newDeckDescription, setNewDeckDescription] = useState("");
  const [newDeckObject, setNewDeckObject] = useState({
    name: "",
    description: "",
  });

  const handleSubmit = (event) => {
    setNewDeckObject({ name: newDeckName, description: newDeckDescription });
    console.log(newDeckObject);
  };

  const history = useHistory();

  const OnClick = (event) => {
    const btnType = event.target.id;
    console.log(btnType);
    if (btnType === "createDeck") {
    }
    if (btnType === "viewDeck") {
			console.log()
      history.push("/decks/:deckId");
    }
    if (btnType === "studyDeck") {
      history.push("/decks/:deckId/study");
    }
    if (btnType === "deleteDeck") {
    }
    if (btnType === "cancelCreateDeck") {
      history.push("/");
    }
    if (btnType === "submitCreateDeck") {
      // take values and make put request
      handleSubmit(event);
    }
    if (btnType === "studyAddCard") {
    }
    if (btnType === "editDeck") {
      history.push("/decks/:deckId/edit");
    }
    if (btnType === "cancelEditDeck") {
      history.push("/decks/:deckId/");
    }
    if (btnType === "doneAddCard") {
      history.push("/decks/:deckId/");
    }
    if (btnType === "saveAddCard") {
    }
  };

  return (
    <Fragment>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
          <Route exact={true} path="/">
            <Home OnClick={OnClick} />
            <DeckList
              OnClick={OnClick}
              setAllDecks={setAllDecks}
              allDecks={allDecks}
            />
          </Route>
          <Route path="/decks/new">
            <NavBar />
            <CreateDeck
              OnClick={OnClick}
              newDeckName={newDeckName}
              setNewDeckName={setNewDeckName}
              newDeckDescription={newDeckDescription}
              setNewDeckDescription={setNewDeckDescription}
              newDeckObject={newDeckObject}
              setAllDecks={setAllDecks}
            />
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <NavBar />
            <AddCard OnClick={OnClick} />
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <NavBar />
            <EditCard OnClick={OnClick} />
          </Route>
          <Route path="/decks/:deckId/edit">
            <NavBar />
            <EditDeck OnClick={OnClick} />
          </Route>
          <Route path="/decks/:deckId/study">
            <NavBar />
            <Study OnClick={OnClick} />
          </Route>
          <Route path="/decks/:deckId">
            <NavBar />
            <CurrentDeck OnClick={OnClick} />
          </Route>

          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
