import React, { Fragment, useState } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import CreateDeck from "./CreateDeck";
import DeckList from "./DeckList";
import Study from "./Study";
import EditDeck from "./EditDeck";
import AddCard from "./AddCard";
import EditCard from "./EditCard";
import { createCard, createDeck, deleteCard, deleteDeck } from "../utils/api";
import ViewDeck from "./ViewDeck";
import NavBarNew from "./NavBarNew";

function Layout() {
  const [allDecks, setAllDecks] = useState({});
  const [currentDeck, setCurrentDeck] = useState({});
  const [currentDeckId, setCurrentDeckId] = useState(0);
  const [newDeckName, setNewDeckName] = useState("");
  const [newDeckDescription, setNewDeckDescription] = useState("");
  const [newDeckObject, setNewDeckObject] = useState({
    name: "",
    description: "",
  });

	const [studyState, setStudyState] = useState({
    deckId: 0,
    cards: null,
    cardNumber: 0,
    flipped: false,
    name: null,
    description: null,
    deckLength: 0,
  });

  const history = useHistory();

  const handleSetDeck = (id) => {
    setCurrentDeckId(id);
  };

  const OnClick = (event) => {
    const btnType = event.target.id;
    const deckId = event.target.parentNode.id;
    console.log(btnType, deckId);
    if (btnType === "viewDeck") {
      setCurrentDeckId(deckId);
      history.push(`/decks/${deckId}`);
    }
    if (btnType === "studyDeck") {
      handleSetDeck(deckId);
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/study`);
    }
    if (btnType === "deleteDeck") {
      const restart = window.confirm(
        "Delete this deck? \n \n You will not be able to recover it."
      );
      if (restart) {
        deleteDeck(deckId);
        history.go(0);
      }
    }
    if (btnType === "cancelCreateDeck") {
      history.push("/");
    }
    if (btnType === "submitCreateDeck") {
      setNewDeckObject({ name: newDeckName, description: newDeckDescription });
      createDeck(newDeckObject);
      history.push("/");
    }
    if (btnType === "addCards") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/cards/new`);
    }
    if (btnType === "deleteCard") {
      const restart = window.confirm(
        "Delete this card? \n \n You will not be able to recover it."
      );
      if (restart) {
        deleteCard(event.target.parentNode.id);
        history.go(0);
      }
    }
    if (btnType === "editDeck") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/edit/`);
    }
    if (btnType === "cancelEditDeck") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/`);
    }
    if (btnType === "editCard") {
      handleSetDeck(deckId);
      history.push(
        `/decks/${deckId ? deckId : currentDeckId}/cards/:cardId/edit`
      );
    }
    if (btnType === "doneAddCard") {
      history.push("/decks/:deckId/");
    }
    if (btnType === "saveAddCard") {
      createCard(currentDeck.id, newCard);
      history.push(`/decks/${currentDeck.id}`);
    }
  };

  return (
    <Fragment>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
          {/* HOME */}
          <Route path="/">
            <Home OnClick={OnClick} />
            <DeckList
              OnClick={OnClick}
              allDecksWithCards={allDecks}
              setAllDecksWithCards={setAllDecks}
            />
          </Route>
          {/* NEW DECK */}
          <Route path="/decks/new">
            <NavBarNew OnClick={OnClick} />
            <CreateDeck
              OnClick={OnClick}
              newDeckName={newDeckName}
              setNewDeckName={setNewDeckName}
              newDeckDescription={newDeckDescription}
              setNewDeckDescription={setNewDeckDescription}
              newDeckObject={newDeckObject}
              setNewDeckObject={setNewDeckObject}
            />
          </Route>
          {/* EDIT CARD */}
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <NavBarNew OnClick={OnClick} />
            <EditCard
              OnClick={OnClick}
              currentDeckId={currentDeckId}
              //editState={editState}
              //setEditState={setEditState}
            />
          </Route>
          {/* EDIT DECK */}
          <Route path="/decks/:deckId/edit">
            <NavBarNew OnClick={OnClick} />
            <EditDeck OnClick={OnClick} />
          </Route>
          {/* STUDY DECK */}
          <Route path="/decks/:deckId/study">
            <NavBarNew
              OnClick={OnClick}
              currentDeck={currentDeck}
              studyState={studyState}
              setStudyState={setStudyState}
            />
            <Study
              OnClick={OnClick}
              studyState={studyState}
              setStudyState={setStudyState}
            />
          </Route>
          {/* ADD CARD */}
          <Route path="/decks/:deckId/cards/new">
            <NavBarNew OnClick={OnClick} />
            <AddCard
              OnClick={OnClick}
              newCard={newCard}
              setNewCard={setNewCard}
              currentDeck={currentDeck}
            />
          </Route>
          {/* VIEW DECK */}
          <Route path="/decks/:deckId">
            <NavBarNew OnClick={OnClick} currentDeck={currentDeck} />
            <ViewDeck
              OnClick={OnClick}
              currentDeck={currentDeck}
              setCurrentDeck={setCurrentDeck}
            />
          </Route>
          {/* NOT FOUND */}
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
