import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { readDeck, updateDeck } from "../utils/api";
import BreadcrumbBar from "./BreadcrumbBar";
import DeckForm from "./DeckForm";
import NavBar from "./NavBar";

function EditDeck({ OnClick, newDeckObject }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();



  const onSubmit = (event) => {
    updateDeck(editState.editedDeck);
    history.push(`/decks/${deckId}`);
  };

  const handleNameChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, name: event.target.value },
    });
  };

  const handleDescriptionChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, description: event.target.value },
    });
  };

  useEffect(() => {
    readDeck(deckId).then((deck) => {
      setEditState({
        ...editState,
        placeholderName: deck.name,
        placeholderDescription: deck.description,
      });
    });
  }, []);

  const OnCancel = () => {
    history.push(`/decks/${deckId}`);
  };

  return (
    <Fragment>
      <BreadcrumbBar
        nav1={editState.placeholderName}
        link1={"test"}
        nav2={"Edit Deck"}
      />
      <h2>Edit Deck</h2>
      <DeckForm OnClick={OnClick} newDeckObject={newDeckObject} />
    </Fragment>
  );
}

export default EditDeck;
