import React, { Fragment, useState } from "react";
import { useHistory } from "react-router";

function Study() {
  const [flipped, setFlipped] = useState(false);
  const [currentCard, setCurrentCard] = useState(1);
  const history = useHistory();

  const cardFront = "front text";
  const cardBack = "back text";
  const totalCards = 3;

  const onFlip = () => {
    console.log(flipped);
    setFlipped((prevState) => !prevState);
  };

  const onNext = () => {
    if (currentCard === totalCards) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart === true) {
        setFlipped(false);
        setCurrentCard(1);
      } else {
        history.push("/");
      }
    } else {
      setCurrentCard((prevValue) => prevValue + 1);
      setFlipped(false);
    }
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const card = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {currentCard} of {totalCards}
        </h5>
        <p className="card-text">{flipped ? cardBack : cardFront}</p>
        {flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: Deck Title</h1>
			card
    </Fragment>
  );
}

export default Study;
