import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";
import { readCard, readDeck } from "../utils/api";

function EditCard({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const cardId = params.cardId;

  const [editCardState, setEditCardState] = useState({ front: "loading" });

  useEffect(() => {
    const deck = readDeck(deckId);
    const card = readCard(cardId).then((card) =>
      setEditCardState({ ...editCardState, front: card.front, back: card.back })
    );
  }, []);

  const handleChange = (event) => {
    if (event.target.parentNode.innerText === "Front") {
      setEditCardState({ ...editCardState, front: event.target.value });
    }
    if (event.target.parentNode.innerText === "Back") {
      setEditCardState({ ...editCardState, back: event.target.value });
    }
  };

  return (
    <Fragment>
      <h3>Edit Card</h3>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Front
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.front}
          value={editCardState.front}
          onChange={handleChange}
        ></textarea>
      </div>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Back
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.back}
          value={editCardState.back}
          onChange={handleChange}
        ></textarea>
      </div>
      <button className="btn btn-secondary" id="doneEditCard" onClick={OnClick}>
        Done
      </button>
      <button className="btn btn-primary" id="submitEditCard" onClick={OnClick}>
        Submit
      </button>
    </Fragment>
  );
}

export default EditCard;
