import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";
import { readCard, readDeck, updateCard } from "../utils/api";
import NavBar from "./NavBar";

function EditCard({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const cardId = params.cardId;

  const [editCardState, setEditCardState] = useState({
    card: { front: "", back: "" },
  });

  useEffect(() => {
    const deck = readDeck(deckId).then((deck) => {
      setEditCardState({
        ...editCardState,
        deckName: deck.name,
      });
    });
  }, []);

  useEffect(() => {
    const card = readCard(cardId).then((card) => {
      setEditCardState({
        ...editCardState,
        front: card.front,
        back: card.back,
        id: card.id,
        deckId: card.deckId,
      });
    });
  }, []);

  const handleChange = (event) => {
    if (event.target.parentNode.innerText === "Front") {
      setEditCardState({ ...editCardState, front: event.target.value });
    }
    if (event.target.parentNode.innerText === "Back") {
      setEditCardState({ ...editCardState, back: event.target.value });
    }
  };

  const onDone = () => {
    console.log(editCardState.deckName);
  };

  const onSubmit = () => {
    updateCard(editCardState.card);
  };

  return (
    <Fragment>
      <NavBar deckName={editCardState.deckName} />
      <h2>Edit Card: {editCardState.deckName}</h2>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Front
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.front}
          value={editCardState.front}
          onChange={handleChange}
        ></textarea>
      </div>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">
          Back
        </label>
        <textarea
          class="form-control"
          id="exampleFormControlTextarea1"
          rows="2"
          placeholder={editCardState.back}
          value={editCardState.back}
          onChange={handleChange}
        ></textarea>
      </div>
      <button
        className="btn btn-secondary mr-2"
        id="doneEditCard"
        onClick={onDone}
      >
        Cancel
      </button>
      <button
        className="btn btn-primary"
        id="submitEditCard"
        onClick={onSubmit}
      >
        Submit
      </button>
    </Fragment>
  );
}

export default EditCard;
