import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

function Study({ OnClick, currentDeckId, setCurrentDeckId }) {
  const params = useParams();
  const deckId = params.deckId;

  const [studyDeck, setStudyDeck] = useState(false);
  const [studyState, setStudyState] = useState({
    studyCardNum: 0,
    flipped: false,
  });

  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/cards?deckId=${deckId}`
        //`http://localhost:5000/cards?deckId=${currentDeckId}`
      );
      const deck = await response.json();
      setStudyDeck(deck);
    }
    loadDeck();
  }, []);

  const onFlip = () => {
    setStudyState({ ...studyState, flipped: !studyState.flipped });
  };

  const onNext = () => {
    setStudyState({
      ...studyState,
      flipped: false,
      studyCardNum: studyState.studyCardNum + 1,
    });
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} id="flipBack" className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const front = studyDeck
    ? studyDeck[studyState.studyCardNum].front
    : "loading...";
  const back = studyDeck
    ? studyDeck[studyState.studyCardNum].back
    : "loading...";

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Card X of Y</h5>
        <p className="card-text">
          {studyState.flipped ? back : front}
          {/*studyDeck ? studyDeck[studyState.studyCardNum].front : "loading..."*/}
          {/*flipped ? back : front*/}
        </p>
        {/*flipped ? bothBtns : flipBtn*/}
        {studyState.flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: Current Deck Name</h1>
      {studyCard}
    </Fragment>
  );
}

export default Study;

