  return (
    <Fragment>
      <BreadcrumbBar nav1={"Create Deck"} />
      <h1>Create Deck</h1>
      <form>
        <div className="mb-3">
          <label htmlFor="deck-name" className="form-label">
            Name
          </label>
          <input
            className="form-control"
            id="name"
            type="text"
            name="name"
            onChange={handleNameChange}
            value={newDeckName}
            placeholder="Deck Name"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="deck-description" className="form-label">
            Description
          </label>
          <textarea
            className="form-control"
            id="description"
            type="password"
            name="description"
            onChange={handleDescriptionChange}
            value={newDeckDescription}
            placeholder="Brief description of the deck"
          />
        </div>
        <button
          type="button"
          className="btn btn-secondary mr-2"
          id="cancelCreateDeck"
          onClick={OnClick}
        >
          Cancel
        </button>
        <button
          type="button"
          className="btn btn-primary"
          id="submitCreateDeck"
          onClick={OnClick}
        >
          Submit
        </button>
      </form>
    </Fragment>
  );
}

export default CreateDeck;
