import React, { Fragment, useEffect } from "react";
import { useHistory, useParams } from "react-router";
import { readDeck } from "../utils/api";
import NavBar from "./NavBar";

function Study({
  studyDeck,
  setStudyDeck,
  studyState,
  setStudyState,
  OnClick,
}) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  //const deckLength = studyDeck ? studyState.cards.length : 0; //"...loading";

  //const temp = readDeck(deckId).then((response) => setStudyDeck(response));
  //console.log(temp);

  /*
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}?_embed=cards`
      );
      const deck = await response.json();
      setStudyDeck(deck.cards);
      setStudyState({ ...studyState, cards: deck.cards, name: deck.name });
    }
    loadDeck();
    console.log(deckLength);
  }, []);

  const onFlip = () => {
    setStudyState({ ...studyState, flipped: !studyState.flipped });
  };

  const onNext = () => {
    if (studyState.studyCardNum + 1 === deckLength) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart) {
        setStudyState({
          ...studyState,
          studyCardNum: 0,
          flipped: false,
        });
      } else {
        history.push("/");
      }
    } else {
      setStudyState({
        ...studyState,
        flipped: false,
        studyCardNum: studyState.studyCardNum + 1,
      });
    }
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} id="flipBack" className="btn btn-primary">
        Next
      </button>
    </Fragment>
  );

  const front = studyDeck
    ? studyDeck[studyState.studyCardNum].front
    : "loading...";

  const back = studyDeck
    ? studyDeck[studyState.studyCardNum].back
    : "loading...";

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {studyState.studyCardNum + 1} of {deckLength}
        </h5>
        <p className="card-text">{studyState.flipped ? back : front}</p>
        {studyState.flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );*/

  const notEnoughCards = (
    <Fragment>
      <h2>Not enough cards.</h2>
      <p>
        You need at least 3 cards to study. There{" "}
        {/*deckLength === 1 ? `is ${deckLength} card` : `are ${deckLength} cards`*/}{" "}
        in this deck.
      </p>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
    </Fragment>
  );

  const path = "TESTING";

  return (
    <Fragment>
      <h1>Study:</h1>
    </Fragment>
  );

  return (
    <Fragment>
      <NavBar OnClick={OnClick} path={studyState.name} />
      <h1>Study: {studyDeck ? studyState.name : "...loading"}</h1>
      {/*studyState.studyCardNum <= deckLength ? studyCard : "...loading"*/}
      {/*deckLength >= 3 ? studyCard : notEnoughCards*/}
    </Fragment>
  );
}

export default Study;

