import React, { useEffect, Fragment } from "react";
import { useHistory } from "react-router";

function DeckList({ OnClick, allDecksWithCards, setAllDecksWithCards }) {
  const history = useHistory();
  const newAllDecksArray = Array.from(allDecksWithCards);
  const listItemDecks = newAllDecksArray.map((deck) =>
    createCardForDeck(deck.name, deck.description, deck.id, deck.cards.length)
  );

  useEffect(() => {
    async function loadDecksWithCards() {
      const response = await fetch(`http://localhost:5000/decks?_embed=cards`);
      const allDecksWithCardsFromAPI = await response.json();
      setAllDecksWithCards(allDecksWithCardsFromAPI);
    }
    loadDecksWithCards();
    //}, [allDecksWithCards]);
  }, [setAllDecksWithCards]);

  async function deleteDeck(event) {
    const restart = window.confirm(
      "Delete this deck? \n \n You will not be able to recover it."
    );
    if (restart) {
      //		deleteDeck(deckId);

      const deckIdToDelete = event.target.parentNode.id;
      const response = await fetch(
        `http://localhost:5000/decks/${deckIdToDelete}`,
        { method: "DELETE" }
      );
    }
  }

  function createCardForDeck(name, description, id, cardsNum) {
    return (
      <Fragment>
        <li className="list-group-item">
          <div className="card border-0">
            <div className="card-body" id={id}>
              <h5 className="card-title">{name}</h5>
              <p>{`${cardsNum} cards`}</p>
              <p className="card-text">{description}</p>
              <button
                to="/"
                className="btn btn-secondary mr-1"
                id="viewDeck"
                onClick={OnClick}
              >
                View
              </button>
              <button
                to="/"
                className="btn btn-primary mr-1"
                id="studyDeck"
                onClick={OnClick}
              >
                Study
              </button>
              <button
                to="/"
                className="btn btn-danger"
                id="deleteDeck"
                onClick={deleteDeck}
              >
                Delete
              </button>
            </div>
          </div>
        </li>
      </Fragment>
    );
  }

  return (
    <Fragment>
      <ul className="list-group">{listItemDecks}</ul>
    </Fragment>
  );
}

export default DeckList;
