      console.log("deck create cancelled, returning home");
    }
    if (btnType === "submitCreateDeck") {
      console.log("creating deck");
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
          <Route exact={true} path="/">
            <Home handleBtns={handleBtns} />
            <DeckList handleBtns={handleBtns} />
          </Route>
          <Route path="/decks/new">
            <CreateDeck handleBtns={handleBtns} />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </>
  );
}

export default Layout;
