import React, { Fragment, useEffect } from "react";
import { useParams } from "react-router";

function CurrentDeck({
  OnClick,
  currentDeck,
  setCurrentDeck,
  currentCardsArray,
  setCurrentCardsArray,
}) {
  const params = useParams();
  const deckId = params.deckId;

  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(`http://localhost:5000/decks/${deckId}`);
      const deckFromAPI = await response.json();
      setCurrentDeck(deckFromAPI);
    }
    loadDeck();
  }, [deckId]);
  // check dependency

  useEffect(() => {
    async function loadCards() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}/cards`
      );
      const cardsFromAPI = await response.json();
      setCurrentCardsArray(cardsFromAPI);
    }
    loadCards();
  }, [deckId]);
  //check dependency

  const deckControlButtons = (
    <Fragment>
      <button className="btn btn-secondary" id="editDeck" onClick={OnClick}>
        Edit
      </button>
      <button className="btn btn-primary" id="studyDeck" onClick={OnClick}>
        Study
      </button>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
      <button className="btn btn-danger" id="deleteDeck" onClick={OnClick}>
        Delete
      </button>
    </Fragment>
  );

  const cardTemplate = (front, back) => (
    <Fragment>
      <li className="list-group-item">
        <div className="row">
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{front}</p>
              </div>
            </div>
          </div>
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{back}</p>
              </div>
              <div className="text-right">
                <button className="btn btn-secondary" id="editCard" onClick={OnClick} >Edit</button>
                <button className="btn btn-danger" id="deleteCard" onClick={OnClick}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      </li>
    </Fragment>
  );

  //take cards array and save variable of card elements
  const newCardsArray = Array.from(currentCardsArray);

  const cardsDisplay = newCardsArray.map((card) =>
    cardTemplate(card.front, card.back)
  );

  return (
    <Fragment>
      <h4>{currentDeck.name}</h4>
      <p>{currentDeck.description}</p>
      {deckControlButtons}
      <h2>Cards</h2>
      <ul className="list-group">{cardsDisplay}</ul>
    </Fragment>
  );
}

export default CurrentDeck;

