import React, { Fragment } from "react";
import NavBar from "./NavBar";

function AddCard({ OnClick, currentDeck, newCard, setNewCard }) {
  const handleFront = (event) => {
    setNewCard({ ...newCard, front: `${event.target.value}` });
  };

  const handleBack = (event) => {
    setNewCard({ ...newCard, back: `${event.target.value}` });
  };

	const OnDone = () => {
		history.push("/decks/deckId")
	}

  return (
    <Fragment>
      <NavBar OnClick={OnClick} deckName={currentDeck.name}  />
      <h3>{currentDeck.name}: Add Card</h3>
      <div className="mb-3">
        <label for="exampleFormControlTextarea1" className="form-label">
          Front
        </label>
        <textarea
          className="form-control"
          id="front"
          type="text"
          name="front"
          onChange={handleFront}
          //value={newCard.front}
          rows="2"
          placeholder="Front side of card"
        ></textarea>
      </div>
      <div className="mb-3">
        <label for="exampleFormControlTextarea1" className="form-label">
          Back
        </label>
        <textarea
          className="form-control"
          id="back"
          type="text"
          name="back"
          onChange={handleBack}
          //value={newCard.back}
          rows="2"
          placeholder="Back side of card"
        ></textarea>
      </div>
      <button className="btn btn-secondary" id="doneAddCard" onClick={OnDone}>
        Done
      </button>
      <button className="btn btn-primary" id="saveAddCard" onClick={OnClick}>
        Save
      </button>
    </Fragment>
  );
}

export default AddCard;
