import React, { Fragment, useEffect, useState } from "react";
import {
  Route,
  Switch,
  useHistory,
  useParams,
  useRouteMatch,
} from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import CreateDeck from "./CreateDeck";
import DeckList from "./DeckList";
import Study from "./Study";
import EditDeck from "./EditDeck";
import AddCard from "./AddCard";
import EditCard from "./EditCard";
import { createDeck, deleteCard, deleteDeck } from "../utils/api";
import ViewDeck from "./ViewDeck";
import NewDeck from "./"

function Layout() {
  const [allDecks, setAllDecks] = useState({});
  const [currentDeck, setCurrentDeck] = useState({});
  const [newDeckObject, setNewDeckObject] = useState({
    name: "",
    description: "",
  });
  const [newCard, setNewCard] = useState({ front: "", back: "" });

  const history = useHistory();

  useEffect(() => {
    setCurrentDeck({});
  }, []);

  // button handler
  const OnClick = (event) => {
    const btnType = event.target.id;
    const deckId = event.target.parentNode.id;
    //console.log(btnType, deckId);
    if (btnType === "viewDeck") {
      history.push(`/decks/${deckId}`);
    }
    if (btnType === "studyDeck") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/study`);
    }
    if (btnType === "cancelCreateDeck") {
      history.push("/");
    }
    if (btnType === "submitCreateDeck") {
      createDeck(newDeckObject).then((deck) =>
        history.push(`/decks/${deck.id}`)
      );
    }
    if (btnType === "addCards") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/cards/new`);
    }
    if (btnType === "deleteCard") {
      const restart = window.confirm(
        "Delete this card? \n \n You will not be able to recover it."
      );
      if (restart) {
        deleteCard(event.target.parentNode.id);
        history.go(0);
      }
    }
    if (btnType === "editDeck") {
      console.log(btnType, event.target.value);
      history.push(`/decks/${event.target.value}/edit`);
    }
    if (btnType === "cancelEditDeck") {
      history.push(`/decks/${deckId ? deckId : currentDeck.id}/`);
    }
    if (btnType === "doneAddCard") {
      history.push("/decks/:deckId/");
    }
  };

  //delete deck
  const onDelete = (event) => {
    const restart = window.confirm(
      "Delete this deck? \n \n You will not be able to recover it."
    );
    if (restart) {
      const deckIdToDelete = parseInt(event.target.parentNode.id);
      deleteDeck(deckIdToDelete);
      history.go(0);
    }
  };

  return (
    <Fragment>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
          {/* HOME */}
          <Route exact={true} path="/">
            <Home OnClick={OnClick} />
            <DeckList
              OnClick={OnClick}
              allDecksWithCards={allDecks}
              setAllDecksWithCards={setAllDecks}
              onDelete={onDelete}
            />
          </Route>
          {/* EDIT CARD */}
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <EditCard
              OnClick={OnClick}
              newCard={newCard}
              setNewCard={setNewCard}
              currentDeck={currentDeck}
            />
          </Route>
          {/* ADD CARD */}
          <Route path="/decks/:deckId/cards/new">
            <AddCard
              OnClick={OnClick}
              newCard={newCard}
              setNewCard={setNewCard}
              currentDeck={currentDeck}
              setCurrentDeck={setCurrentDeck}
            />
          </Route>
          {/* NEW DECK */}
          <Route path={`/decks/new`}>
            <CreateDeck
              OnClick={OnClick}
              newDeckObject={newDeckObject}
              setNewDeckObject={setNewDeckObject}
            />
          </Route>
          {/* EDIT DECK */}
          <Route path={`/decks/:deckId/edit`}>
            <EditDeck OnClick={OnClick} newDeckObject={newDeckObject} />
          </Route>
          {/* STUDY DECK */}
          <Route path={`/decks/:deckId/study`}>
            <Study currentDeck={currentDeck} setCurrentDeck={setCurrentDeck} />
          </Route>
          {/* VIEW DECK */}
          <Route path="/decks/:deckId">
            <ViewDeck
              OnClick={OnClick}
              currentDeck={currentDeck}
              setCurrentDeck={setCurrentDeck}
              onDelete={onDelete}
            />
          </Route>

          {/* NOT FOUND */}
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
