import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { readDeck, updateDeck } from "../utils/api";
import BreadcrumbBar from "./BreadcrumbBar";
import NavBar from "./NavBar";

function EditDeck({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  const [editState, setEditState] = useState({
    decks: { name: "", description: "" },
    placeholderName: "...loading",
    placeholderDescription: "...loading",
    editedDeck: {
      id: deckId,
      name: "",
      description: "",
    },
  });

  const onSubmit = (event) => {
    updateDeck(editState.editedDeck);
    history.push(`/decks/${deckId}`);
  };

  const handleNameChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, name: event.target.value },
    });
  };

  const handleDescriptionChange = (event) => {
    setEditState({
      ...editState,
      editedDeck: { ...editState.editedDeck, description: event.target.value },
    });
  };

  useEffect(() => {
    readDeck(deckId).then((deck) => {
      setEditState({
        ...editState,
        placeholderName: deck.name,
        placeholderDescription: deck.description,
      });
    });
  }, []);

  const OnCancel = () => {
    history.push(`/decks/${deckId}`);
  };

	return

  return (
    <Fragment>
      <BreadcrumbBar
        nav1={editState.placeholderName}
        link1={"test"}
        nav2={"Edit Deck"}
      />
      <div className="mb-3">
        <h2>Edit Deck</h2>
        <label htmlFor="name" className="form-label">
          Name
        </label>
        <input
          type="text"
          className="form-control"
          id="name"
          placeholder={editState.placeholderName}
          onChange={handleNameChange}
          value={editState.editedDeck.name}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="description"
          rows="3"
          placeholder={editState.placeholderDescription}
          onChange={handleDescriptionChange}
          value={editState.editedDeck.description}
        ></textarea>
        <button
          className="btn btn-secondary mr-2 mt-2"
          id="cancelEditDeck"
          onClick={OnCancel}
        >
          Cancel
        </button>
        <button
          className="btn btn-primary mt-2"
          id="submitEditDeck"
          onClick={onSubmit}
        >
          Submit
        </button>
      </div>
    </Fragment>
  );
}

export default EditDeck;
