import React, { useEffect } from "react";
import { useParams } from "react-router";
import { readDeck } from "../utils/api";

function Study({ currentDeck, setCurrentDeck }) {
  const params = useParams();
  const deckId = params.deckId;

  useEffect(() => {
    readDeck(deckId).then((deck) => {
      setCurrentDeck({
        id: deck.id,
        name: deck.name,
        description: deck.description,
        cards: deck.cards,
        cardNumber: 0,
      });
    });
  }, []);

  const onFlip = () => {};

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {currentDeck ? currentDeck.cardNumber : "loading"} of{" "}
          {currentDeck ? currentDeck.cards.length : "loading"}
        </h5>
        <p>{currentDeck ? "front" : "there"}</p>
        {flipBtn}
      </div>
    </div>
  );

  return (
    <>
      <h1>Study: {currentDeck ? currentDeck.name : "loading"}</h1>
      {studyCard}
    </>
  );
}

export default Study;

