import React, { useState } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "./Home";
import CreateDeck from "./CreateDeck";
import DeckList from "./DeckList";
import NewDeck from "./NewDeck";

function Layout() {
  const [state, setState] = useState({
    decks: [],
  });

  const [decks, setDecks] = useState({});
  let deckCount = 0;

  let deckList = [];
  const history = useHistory();

  const OnClick = (event) => {
    const btnType = event.target.id;
    //Home Buttons
    if (btnType === "createDeck") {
      console.log(btnType);
    }
    if (btnType === "viewDeck") {
      console.log(btnType);
      history.push("/decks/:deckId");
    }
    if (btnType === "studyDeck") {
      console.log(btnType);
      history.push("/decks/:deckId/study");
    }
    if (btnType === "deleteDeck") {
      console.log(btnType);
    }
    //CreateDeckButtons
    if (btnType === "cancelCreateDeck") {
      console.log(btnType);
      history.push("/");
    }
    if (btnType === "submitCreateDeck") {
      console.log(btnType);
      //create new deck and append to list
      setDecks({ ...decks, [btnType]: deckCount + 1 });
      deckCount = deckCount + 1;
      console.log(deckCount);
    }
  };

  //console.log(state);

  //console.log("Here's the current State:", state);

  return (
    <>
      <Header />
      <div className="container">
        {/* TODO: Implement the screen starting here */}
        <Switch>
          <Route exact={true} path="/">
            <Home OnClick={OnClick} />
            <DeckList OnClick={OnClick} />
          </Route>
          <Route path="/decks/new">
            <CreateDeck OnClick={OnClick} />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </>
  );
}

export default Layout;
