import React, { useEffect, Fragment } from "react";
//import NewDeck from "./NewDeck";

function DeckList({ OnClick, setAllDecks, allDecks }) {
  //const deckList = [<NewDeck />];

  useEffect(() => {
    async function loadAllDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const allDecksFromAPI = await response.json();
      setAllDecks(allDecksFromAPI);
    }
    loadAllDecks();
  }, []);
  //may have to change dependency

  //add info to decks array

  const allDecksArray = Array.from(allDecks);

  //get number of cards per deck
  useEffect(() => {
    async function loadDecksWithCards() {
			const response = await fetch(`http://localhost:5000/decks?_embed=cards`);
			const allDecksWithCardsFromAPI = await response.json();
			setAllDecksWithCards(allDecksWithCardsFromAPI);
		}
		load
  });

  function createCardForDeck(name, description, id) {
    return (
      <Fragment>
        <li className="list-group-item">
          <div className="card border-0">
            <div className="card-body" id={id}>
              <h5 className="card-title">{name}</h5>
              <p>3 cards</p>
              <p className="card-text">{description}</p>
              <button
                to="/"
                className="btn btn-secondary"
                id="viewDeck"
                onClick={OnClick}
              >
                View
              </button>
              <button
                to="/"
                className="btn btn-primary"
                id="studyDeck"
                onClick={OnClick}
              >
                Study
              </button>
              <button
                to="/"
                className="btn btn-danger"
                id="deleteDeck"
                onClick={OnClick}
              >
                Delete
              </button>
            </div>
          </div>
        </li>
      </Fragment>
    );
  }

  const listItems = allDecksArray.map((deck) =>
    createCardForDeck(deck.name, deck.description, deck.id)
  );

  return (
    <Fragment>
      <ul className="list-group">{listItems}</ul>
    </Fragment>
  );
}

export default DeckList;
