import React, { Fragment, useEffect } from "react";
import { useHistory, useParams } from "react-router";
import { readDeck } from "../utils/api";
import NavBar from "./NavBar";

function ViewDeck({ OnClick, currentDeck, setCurrentDeck }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  useEffect(() => {
    const deck = readDeck(deckId);
    deck.then((deck) => {
      setCurrentDeck({
        name: deck.name,
        description: deck.description,
        cards: deck.cards,
        id: deck.id,
      });
    });
  }, []);

  async function deleteDeck(event) {
    console.log(event.target);
    /* const restart = window.confirm(
      "Delete this deck? \n \n You will not be able to recover it."
    );
    if (restart) {
      const deckIdToDelete = event.target.parentNode.id;
      const response = await fetch(
        `http://localhost:5000/decks/${deckIdToDelete}`,
        { method: "DELETE" }
      );
      history.go(0);
    }
  */
  }

  const deckControlButtons = (
    <Fragment>
      <button className="btn btn-secondary" id="editDeck" onClick={OnClick}>
        Edit
      </button>
      <button className="btn btn-primary" id="studyDeck" onClick={OnClick}>
        Study
      </button>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
      <button className="btn btn-danger" id="deleteDeck" onClick={OnClick}>
        Delete
      </button>
    </Fragment>
  );

  const OnEditCard = (event) => {
    const cardId = event.target.parentNode.id;
    history.push(`/decks/${deckId}/cards/${cardId}/edit`);
  };

  const cardTemplate = (front, back, id) => (
    <Fragment key={id}>
      <li className="list-group-item">
        <div className="row">
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{front}</p>
              </div>
            </div>
          </div>
          <div className="col-sm-6">
            <div className="card border-0">
              <div className="card-body">
                <p className="card-text">{back}</p>
              </div>
              <div className="text-right" id={`${id}`}>
                <button
                  className="btn btn-secondary"
                  id="editCard"
                  onClick={OnEditCard}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger"
                  id="deleteCard"
                  onClick={OnClick}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      </li>
    </Fragment>
  );

  const cards = currentDeck.cards
    ? currentDeck.cards.map((card) =>
        cardTemplate(card.front, card.back, card.id)
      )
    : "loading...";

  return (
    <Fragment>
      <NavBar OnClick={OnClick} deckName={currentDeck.name} />
      <h4>{currentDeck.name}</h4>
      <p>{currentDeck.description}</p>
      {deckControlButtons}
      <h2>Cards</h2>
      <ul className="list-group">{cards}</ul>
    </Fragment>
  );
}

export default ViewDeck;
