import React, { Fragment, useState } from "react";
import { useHistory, useParams } from "react-router";

function Study({ OnClick, currentCardsArray, currentDeck }) {
  const params = useParams();
  const deckId = params.deckId;

  /*
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(`http://localhost:5000/decks/?_embed=cards`);
      const deckFromAPI = await response.json();
      setCurrentDeck(deckFromAPI);
    }
    loadDeck();
  }, [deckId]);*/
  // check dependency

  const [flipped, setFlipped] = useState(false);
  const [currentCard, setCurrentCard] = useState(1);
  const history = useHistory();

  const cardFront = "front text";
  const cardBack = "back text";
  const totalCards = 1;

  const onFlip = () => {
    console.log(flipped);
    setFlipped((prevState) => !prevState);
  };

  const onNext = () => {
    if (currentCard === totalCards) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart === true) {
        setFlipped(false);
        setCurrentCard(1);
      } else {
        history.push("/");
      }
    } else {
      setCurrentCard((prevValue) => prevValue + 1);
      setFlipped(false);
    }
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const card = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {currentCard} of {totalCards}
        </h5>
        <p className="card-text">{flipped ? cardBack : cardFront}</p>
        {flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  const notEnoughCards = (
    <Fragment>
      <h2>Not enough cards.</h2>
      <p>
        You need at least 3 cards to study. There{" "}
        {totalCards === 1 ? `is ${totalCards} card` : `are ${totalCards} cards`}{" "}
        in this deck.
      </p>
      <button className="btn btn-primary" id="studyAddCard" onClick={OnClick}>
        Add Cards
      </button>
    </Fragment>
  );

  return (
    <Fragment>
      <h1>Study: Deck Title</h1>
      {totalCards < 3 ? notEnoughCards : card}
    </Fragment>
  );
}

export default Study;
