import React, { Fragment, useState } from "react";

function Study() {
  const [flipped, setFlipped] = useState(false);

  const onFlip = () => {
    console.log(flipped);
		setFlipped((prevState) => !prevState)
  };
  const cardFront = "front text";
  const cardBack = "back text";
  const currentCard = 1;
  const totalCards = 3;

  return (
    <Fragment>
      <h1>Study: Deck Title</h1>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">
            Card {currentCard} of {totalCards}
          </h5>
          <p className="card-text">{cardFront}</p>
          <button onClick={onFlip} className="btn btn-secondary">
            Flip
          </button>
        </div>
      </div>
    </Fragment>
  );
}

export default Study;
