import React, { Fragment, useState } from "react";

function CreateDeck({
  OnClick,
  newDeckName,
  setNewDeckName,
  newDeckDescription,
  setNewDeckDescription,
	allDecks,
}) {
  const handleNameChange = (event) => {
    setNewDeckName(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setNewDeckDescription(event.target.value);
  };

  useEffect(() => {
    async function putNewDeck() {
      const data = { ...allDecks, newDeckObject };
      const response = await fetch(`http://localhost:5000/decks`, {
        method: "PUT",
        body: JSON.stringify(data),
      });
      const newDecksFromAPI = await response.json();
      setAllDecks(newDecksFromAPI);
    }
    putNewDeck();
  });

  return (
    <Fragment>
      <h1>Create Deck</h1>
      <form>
        <div className="mb-3">
          <label htmlFor="deck-name" className="form-label">
            Name
          </label>
          <input
            className="form-control"
            id="name"
            type="text"
            name="name"
            onChange={handleNameChange}
            value={newDeckName}
            placeholder="Name"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="deck-description" className="form-label">
            Description
          </label>
          <textarea
            className="form-control"
            id="exampleInputPassword1"
            type="password"
            name="description"
            onChange={handleDescriptionChange}
            value={newDeckDescription}
            placeholder="Brief description of the deck"
          />
        </div>
        <button
          type="button"
          className="btn btn-secondary"
          id="cancelCreateDeck"
          onClick={OnClick}
        >
          Cancel
        </button>
        <button
          type="button"
          className="btn btn-primary"
          id="submitCreateDeck"
          onClick={OnClick}
        >
          Submit
        </button>
      </form>
    </Fragment>
  );
}

export default CreateDeck;
