import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";
import { readDeck } from "../utils/api";

function ViewDeck({ OnClick }) {
  const [currentDeck, setCurrentDeck] = useState({});
  const params = useParams();
  const deckId = params.deckId;

  useEffect(() => {
    const temp = readDeck(deckId);

    temp.then((deck) => {
      console.log(deck);
      setCurrentDeck({
        name: deck.name,
        description: deck.description,
        cards: deck.cards,
        id: deck.id,
      });
    });
  }, []);

  const deckControlButtons = (
    <Fragment>
      <button className="btn btn-secondary" id="editDeck" onClick={OnClick}>
        Edit
      </button>
      <button className="btn btn-primary" id="studyDeck" onClick={OnClick}>
        Study
      </button>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
      <button className="btn btn-danger" id="deleteDeck" onClick={OnClick}>
        Delete
      </button>
    </Fragment>
  );

  return (
    <Fragment>
      <h4>{currentDeck.name}</h4>
      <p>{currentDeck.description}</p>
      {deckControlButtons}
      <h2>Cards</h2>
    </Fragment>
  );
}

export default ViewDeck;
