import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { readCard, readDeck, updateCard } from "../utils/api";
import CardForm from "./CardForm";

function EditCard({ OnClick, newCard, setNewCard }) {
  const params = useParams();
  const deckId = params.deckId;
  const cardId = params.cardId;
  const history = useHistory();
  //console.log(params);

  const [editCardState, setEditCardState] = useState({
    card: { front: "", back: "" },
  });

  useEffect(() => {
    async function loadPage() {
      const deck = await readDeck(deckId).then((deck) => {
        console.log(deck);
        setEditCardState({
          ...editCardState,
          deckName: deck.name,
          cards: deck.cards,
          id: deck.id,
        });
      });

      const card = await readCard(cardId).then((card) => {
        setEditCardState({
          ...editCardState,
          front: card.front,
          back: card.back,
          id: card.id,
          deckId: card.deckId,
        });
      });
    }
    loadPage();
  }, []);

  useEffect(() => {
    /*const card = readCard(cardId).then((card) => {
      setEditCardState({
        ...editCardState,
        front: card.front,
        back: card.back,
        id: card.id,
        deckId: card.deckId,
      });
    });*/
  }, []);

  const handleChange = (event) => {
    if (event.target.parentNode.innerText === "Front") {
      setEditCardState({ ...editCardState, front: event.target.value });
    }
    if (event.target.parentNode.innerText === "Back") {
      setEditCardState({ ...editCardState, back: event.target.value });
    }
  };

  const onCancel = () => {
    console.log(editCardState.deckName);
    history.push(`/decks/${deckId}`);
  };

  const onSubmit = () => {
    const editedCard = {
      front: editCardState.front,
      back: editCardState.back,
      id: cardId,
      deckId: deckId,
    };
    updateCard(editedCard);
    history.push(`/decks/${deckId}`);
  };

  return (
    <Fragment>
      <h2 className="mt-2">Edit Card</h2>
      <CardForm newCard={newCard} setNewCard={setNewCard} />
      <button
        className="btn btn-secondary mr-2"
        id="doneEditCard"
        onClick={onCancel}
      >
        Cancel
      </button>
      <button
        className="btn btn-primary"
        id="submitEditCard"
        onClick={onSubmit}
      >
        Submit
      </button>
    </Fragment>
  );
}

export default EditCard;
