import React, { Fragment, useEffect } from "react";
import { useHistory, useParams } from "react-router";
import { createCard, readDeck } from "../utils/api";
import NavBar from "./NavBar";
import Form from "./Form";

function AddCard({
  OnClick,
  setCurrentDeck,
  currentDeck,
  newCard,
  setNewCard,
}) {
  const history = useHistory();
  const params = useParams();
  const deckId = params.deckId;

  useEffect(() => {
    const deck = readDeck(deckId);
    deck.then((deck) => {
      setCurrentDeck({ ...currentDeck, name: deck.name });
    });
  }, []);

  const handleFront = (event) => {
    setNewCard({ ...newCard, front: `${event.target.value}` });
  };

  const handleBack = (event) => {
    setNewCard({ ...newCard, back: `${event.target.value}` });
  };

  const OnDone = () => {
    history.push(`/decks/${deckId}`);
  };

  const OnSave = () => {
    createCard(currentDeck.id, newCard);
    setNewCard({ ...newCard, front: "", back: "" });
    history.push(`/decks/${deckId}/cards/new`);
  };

  return (
    <Fragment>
      <NavBar OnClick={OnClick} deckName={currentDeck.name} />
      <h3>{currentDeck.name}: Add Card</h3>

			<Form />


      <button className="btn btn-secondary" id="doneAddCard" onClick={OnDone}>
        Done
      </button>
      <button className="btn btn-primary" id="saveAddCard" onClick={OnSave}>
        Save
      </button>
    </Fragment>
  );
}

export default AddCard;
