import React, { Fragment, useEffect } from "react";
import { useHistory, useParams } from "react-router";
import { Link } from "react-router-dom";
import { readDeck } from "../utils/api";
import NavBar from "./NavBar";

function Study({ OnClick, studyState, setStudyState }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  let front = "loading...";
  let back = "loading...";

  useEffect(() => {
    const deck = readDeck(deckId);
    deck.then((deck) => {
      setStudyState({
        ...studyState,
        deckId: deck.id,
        cards: deck.cards,
        name: deck.name,
        description: deck.description,
        deckLength: deck.cards.length,
      });
    });
    console.log(studyCard.cards);
    async function loadText() {
      front =   studyState.cards[studyState.cardNumber].front;
      // ? studyState.cards[studyState.cardNumber].front
      // : "loading...";
      back = studyState.cards
        ? studyState.cards[studyState.cardNumber].back
        : "loading...";
    }
    loadText();
  }, []);

  const onFlip = () => {
    setStudyState({ ...studyState, flipped: !studyState.flipped });
  };

  const onNext = () => {
    if (studyState.cardNumber + 1 === studyState.deckLength) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart) {
        history.go(0);
      } else {
        history.push("/");
      }
    }
    setStudyState({
      ...studyState,
      cardNumber: studyState.cardNumber + 1,
      flipped: false,
    });
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} id="flipBack" className="btn btn-primary">
        Next
      </button>
    </Fragment>
  );

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {studyState.cardNumber + 1} of {studyState.deckLength}
        </h5>
        <p>{studyState.flipped ? back : front}</p>
        {studyState.flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  const notEnoughCards = (num) => {
    let message = "init";
    if (num === 1) {
      message = "There is 1 card in this deck.";
    } else {
      message = `There are ${num} cards in this deck.`;
    }
    return (
      <Fragment>
        <h2>Not enough cards.</h2>
        <p>You need at least 3 cards to study. {message}</p>
        <Link
          to={`/decks/${deckId}/cards/new`}
          className="btn btn-primary"
          id="addCards"
        >
          Add Cards
        </Link>
      </Fragment>
    );
  };
  return (
    <Fragment>
      <NavBar OnClick={OnClick} deckName={studyState.name} />
      <h1>Study: {studyState.name ? studyState.name : "...loading"}</h1>
      {studyState.deckLength < 3
        ? notEnoughCards(studyState.deckLength)
        : studyCard}
    </Fragment>
  );
}

export default Study;

