import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

function Study({ OnClick, currentDeckId, setCurrentDeckId }) {
  const deckId = useParams()

	const handleSetDeck = (id) => {
    setCurrentDeckId(id);
  };
  //
  const [temp, setTemp] = useState({ front: "front", back: "back" });
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/cards?deckId=${currentDeckId}`
      );
      const deck = await response.json();
      console.log(deck);
      setTemp(deck);
    }
    loadDeck();
  }, []);

  //console.log(temp);

  const bothBtns = (
    <Fragment>
      <button onClick={OnClick} id="flipFront" className="btn btn-secondary">
        Flip
      </button>
      <button onClick={OnClick} id="flipBack" className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Card X of Y</h5>
        <p>{"temp"}</p>
        <p className="card-text">front or back{/*flipped ? back : front*/}</p>
        {/*flipped ? bothBtns : flipBtn*/}
        {bothBtns}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: Current Deck Name</h1>
      {studyCard}
    </Fragment>
  );
}

export default Study;
