import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { listCards, readDeck } from "../utils/api";

function Study({
  OnClick,
  currentDeck,
  setCurrentDeck,
  deckLength,
  setDeckLength,
  currentCardsArray,
  allDecksWithCards,
}) {
  const params = useParams();
  const history = useHistory();
  const deckId = params.deckId;
  //const temp = readDeck(deckId).then(console.log);

  const [flipped, setFlipped] = useState(false);
  const [currentCardNumber, setCurrentCardNumber] = useState(0);

  const [front, setFront] = useState("front");
  const [back, setBack] = useState("back");
  const [currentCards, setCurrentCards] = useState([]);

  //sets current deck with it's cards
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}?_embed=cards`
      );
      const deckFromAPI = await response.json();
      setCurrentDeck(deckFromAPI);
    }
    loadDeck();
    console.log(currentDeck);
    console.log(deckId);
    //}, [deckId, currentDeck]); //this is right but makes computer slow while running server
  }, [deckId]);

  useEffect(() => {
    async function loadCards() {
      const response = await fetch(
        `http://localhost:5000/cards?deckId=${deckId}`
      );
      const cardsFromAPI = await response.json();
      setCurrentCards(cardsFromAPI);
    }
    loadCards();
  }, []);

  useEffect(() => {
    console.log("HERE", allDecksWithCards);
    const temp = allDecksWithCards.find((deck) => {
      if (`${deck.id}` === deckId) {
        return deck;
      }
    });
    console.log("TEMP", temp.cards[(currentCardNumber - 1)].front);
    setFront(temp.cards[0].front);
    setBack(temp.cards[0].back);
    setCurrentCardNumber(temp.cards[0].id);
  }, [currentCardsArray]);

  const onFlip = () => {
    setFlipped((prevState) => !prevState);
  };

  const onNext = () => {
    if (currentCardNumber === deckLength) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart === true) {
        setFlipped(false);
        setCurrentCardNumber(1);
      } else {
        history.push("/");
      }
    } else {
      setCurrentCardNumber((prevValue) => prevValue + 1);
      setFlipped(false);
    }
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const card = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {currentCardNumber} of {deckLength}
        </h5>
        <p className="card-text">{flipped ? back : front}</p>
        {flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  const notEnoughCards = (
    <Fragment>
      <h2>Not enough cards.</h2>
      <p>
        You need at least 3 cards to study. There{" "}
        {deckLength === 1 ? `is ${deckLength} card` : `are ${deckLength} cards`}{" "}
        in this deck.
      </p>
      <button className="btn btn-primary" id="addCards" onClick={OnClick}>
        Add Cards
      </button>
    </Fragment>
  );

  return (
    <Fragment>
      <h1>Study: {"current deck name"}</h1>
      {deckLength < 3 ? notEnoughCards : card}
    </Fragment>
  );
}

export default Study;

