import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { listCards, readDeck } from "../utils/api";
import NavBar from "./NavBar";

function Study({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  const [studyState, setStudyState] = useState({
    deckId: 0,
    cards: {},
    cardNumber: 0,
    flipped: false,
    name: null,
    description: null,
  });

  useEffect(() => {
    const deck = readDeck(deckId);
    deck.then((deck) => {
      setStudyState({
        ...studyState,
        deckId: deck.id,
        cards: deck.cards,
        name: deck.name,
        description: deck.description,
      });
    });
  }, []);

  console.log(studyState);

  const onFlip = () => {
    setStudyState({ ...studyState, flipped: !studyState.flipped });
  };

  //const front = studyState.cards
  //? studyState.cards[studyState.cardNumber].front
  //: "loading...";

  const front = studyState.cards[studyState.cardNumber].front;

  const back = studyState.cards
    ? studyState.cards[studyState.cardNumber].back
    : "loading...";

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const studyCard = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {1} of {7}
        </h5>
        <p>{studyState.flipped ? back : front}</p>
        {flipBtn}
      </div>
    </div>
  );

  return (
    <Fragment>
      <h1>Study: {studyState.name ? studyState.name : "...loading"}</h1>
      {studyCard}
    </Fragment>
  );
} //}{" "} //      in this deck. //  </p> //<button className="btn btn-primary" id="addCards" onClick={OnClick}> //Add Cards // </button> //</Fragment> //); //const path = "TESTING";

