import React, { useState, useEffect, Fragment } from "react";
import NewDeck from "./NewDeck";

function DeckList({ OnClick, setAllDecks, allDecks }) {
  const deckList = [<NewDeck />];

  useEffect(() => {
    async function loadAllDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const allDecksFromAPI = await response.json();
      setAllDecks(allDecksFromAPI);
    }
    loadAllDecks();
  }, []);

  //console.log(allDecks);

  const listAllDecks = (name, description) => {
    return (
      <Fragment>
        <h1>Test</h1>
        <li className="list-group-item">
          <div className="card border-0">
            <div className="card-body">
              <h5 className="card-title">{name}</h5>
              <p>3 cards</p>
              <p className="card-text">{description}</p>
              <button
                to="/"
                className="btn btn-secondary"
                id="viewDeck"
                onClick={OnClick}
              >
                View
              </button>
              <button
                to="/"
                className="btn btn-primary"
                id="studyDeck"
                onClick={OnClick}
              >
                Study
              </button>
              <button
                to="/"
                className="btn btn-danger"
                id="deleteDeck"
                onClick={OnClick}
              >
                Delete
              </button>
            </div>
          </div>
        </li>
      </Fragment>
    );
  };

  const allDecksArray = Array.from(allDecks);
  const temp = allDecksArray.map((deck) => {
    // console.log(deck.name);
    listAllDecks(deck.name, deck.description);
  });
  console.log(temp);

  return (
    <Fragment>
      <ul className="list-group">{temp}</ul>
    </Fragment>
  );
}

export default DeckList;
