import React, { Fragment, useEffect, useState } from "react";

function TestDeck() {
  const [testDecks, setTestDecks] = useState({});

  useEffect(() => {
    async function loadDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const testDecksFromAPI = await response.json();
      setTestDecks(testDecksFromAPI);
    }
    loadDecks();
  }, []);

  let testText = "";

  const decksArray = Array.from(testDecks);
  console.log(decksArray);
  for (let i = 0; i < decksArray.length; i++) {
    const deck = decksArray[i];

		const title = deck.title;
    const desc = deck.description;

		return DeckOne(title, desc)

  }

  const totalCards = decksArray.map((deck) => deck.id).length;
  const deckTitle = decksArray.map((deck) => deck.name);

  function DeckOne() {
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{deckTitle}</h5>
          <p>{totalCards} CARDDS</p>
          <p className="card-text">Test text</p>
        </div>
      </div>
    );
  }

  function NewDeck({ OnClick }) {
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">{deckTitle}</h5>
          <p>{totalCards} cards</p>
          <p className="card-text">Test text</p>
        </div>
      </div>
    );
  }

  return (
    <Fragment>
      <h1>TEST</h1>
      <DeckOne />
    </Fragment>
  );
}

export default TestDeck;
