import React, { Fragment, useState } from "react";
import { useHistory } from "react-router";

function Study() {
  const [flipped, setFlipped] = useState(false);
  const [currentCard, setCurrentCard] = useState(1);
	const history = useHistory()

  const cardFront = "front text";
  const cardBack = "back text";
  //let currentCard = 1;
  const totalCards = 3;

  const onFlip = () => {
    console.log(flipped);
    setFlipped((prevState) => !prevState);
  };

  const onNext = () => {
    if (currentCard === totalCards) {
      const restart = window.confirm("Restart cards?");
			if (restart === true) {
				//restart
			} else {
				//return home 

			}

    } else {
      setCurrentCard((prevValue) => prevValue + 1);
    }
    setFlipped(false);
  };

  const buttons = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothButtons = (
    <Fragment>
      <button onClick={onFlip} className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  return (
    <Fragment>
      <h1>Study: Deck Title</h1>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">
            Card {currentCard} of {totalCards}
          </h5>
          <p className="card-text">{flipped ? cardBack : cardFront}</p>
          {flipped ? bothButtons : buttons}
        </div>
      </div>
    </Fragment>
  );
}

export default Study;
