import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";
import { updateDeck } from "../utils/api";

function EditDeck({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;

  // load deck name and desc
  // change it to form value
  //const [decks, setDecks] = useState({});
  const [editDeck, setEditDeck] = useState({});

  useEffect(() => {
    async function loadDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const decksFromAPI = await response.json();
      setEditDeck(decksFromAPI);
    }
    loadDecks();
  }, []);

  //  updateDeck("updated deck");

  return (
    <Fragment>
      <div className="mb-3">
        <h1>Edit Deck</h1>
        <label htmlFor="exampleFormControlInput1" className="form-label">
          Name
        </label>
        <input
          type="email"
          className="form-control"
          id="exampleFormControlInput1"
          placeholder={`${editDeck[deckId].name}`}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="exampleFormControlTextarea1" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="exampleFormControlTextarea1"
          rows="3"
          placeholder={`${editDeck[deckId].description}`}
        ></textarea>
        <button
          className="btn btn-secondary"
          id="cancelEditDeck"
          onClick={OnClick}
        >
          Cancel
        </button>
        <button
          className="btn btn-primary"
          id="submitEditDeck"
          onClick={OnClick}
        >
          Submit
        </button>
      </div>
    </Fragment>
  );
}

export default EditDeck;
