import React, { Fragment } from "react";

function CardForm({ newCard, setNewCard, cardFront, cardBack }) {
  const handleFront = (event) => {
    setNewCard({ ...newCard, front: `${event.target.value}` });
  };

  const handleBack = (event) => {
    setNewCard({ ...newCard, back: `${event.target.value}` });
  };
  return (
    <Fragment>
      <div className="mb-3">
        <label for="Front" className="form-label">
          Front
        </label>
        <textarea
          className="form-control"
          id="front"
          type="text"
          name="front"
          onChange={handleFront}
          value={newCard.front}
          rows="2"
          placeholder={cardFront}
        ></textarea>
      </div>
      <div className="mb-3">
        <label for="Back" className="form-label">
          Back
        </label>
        <textarea
          className="form-control"
          id="back"
          type="text"
          name="back"
          onChange={handleBack}
          value={newCard.back}
          rows="2"
          placeholder={card}
        ></textarea>
      </div>
    </Fragment>
  );
}

export default CardForm;
