import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import { updateDeck } from "../utils/api";

function EditDeck({ OnClick }) {
  const params = useParams();
  const deckId = params.deckId;
  const history = useHistory();

  const [editedDeck, setEditedDeck] = useState({
    name: "",
    description: "",
  });

  const [editState, setEditState] = useState({
    decks: { name: "", description: "" },
    placeholderName: "...loading",
    placeholderDescription: "...loading",
  });

  const onSubmit = (event) => {
    console.log(editedDeck);
		updateDeck(editedDeck)
    //history.push(`/decks/${deckId}`);
  };

  const handleNameChange = (event) => {
    //console.log(event.target.value);
    setEditedDeck({ ...editedDeck, name: event.target.value });
  };

  const handleDescriptionChange = (event) => {
    //console.log(event.target.value);
    setEditedDeck({ ...editedDeck, description: event.target.value });
  };

  useEffect(() => {
    async function loadDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const decksFromAPI = await response.json();
      //setEditedDeck(decksFromAPI);
      //console.log(editDeck);
      setEditState({
        ...editState,
        decks: decksFromAPI,
        placeholderName: decksFromAPI[deckId - 1].name,
        placeholderDescription: decksFromAPI[deckId - 1].description,
      });
    }
    loadDecks();
  }, []);

  return (
    <Fragment>
      <div className="mb-3">
        <h1>Edit Deck</h1>
        <label htmlFor="name" className="form-label">
          Name
        </label>
        <input
          type="text"
          className="form-control"
          id="name"
          placeholder={editState.placeholderName}
          onChange={handleNameChange}
          value={editedDeck.name}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="description" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="description"
          rows="3"
          placeholder={editState.placeholderDescription}
          onChange={handleDescriptionChange}
          value={editedDeck.description}
        ></textarea>
        <button
          className="btn btn-secondary"
          id="cancelEditDeck"
          onClick={OnClick}
        >
          Cancel
        </button>
        <button
          className="btn btn-primary"
          id="submitEditDeck"
          onClick={onSubmit}
        >
          Submit
        </button>
      </div>
    </Fragment>
  );
}

export default EditDeck;
