import React, { Fragment } from "react";

function CardForm({
  editCardState,
  setEditCardState,
  newCard,
  setNewCard,
  cardFront,
  cardBack,
}) {
  const handleChange = (event) => {
    //console.log(newCard);
    //setNewCard({ ...newCard, [event.target.name]: event.target.value });
    setEditCardState({
      ...editCardState,
      [event.target.name]: event.target.value,
    });
  };


  return (
    <Fragment>
      <div className="mb-3">
        <label htmlFor="Front" className="form-label">
          Front
        </label>
        <textarea
          className="form-control"
          id="front"
          type="text"
          name="front"
          onChange={handleChange}
          value={editCardState.front}
          rows="2"
          //placeholder={editCardState.front}
        ></textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="Back" className="form-label">
          Back
        </label>
        <textarea
          className="form-control"
          id="back"
          type="text"
          name="back"
          onChange={handleChange}
          value={editCardState.back}
          rows="2"
          //placeholder={editCardState.back}
        ></textarea>
      </div>
    </Fragment>
  );
}

export default CardForm;
