import React from "react";
import { useRouteMatch } from "react-router";

function DeckForm({ OnClick, newDeckObject, setNewDeckObject }) {
  // create deck handler
	const handleChange = (event) => {
    setNewDeckObject({
      ...newDeckObject,
      [event.target.name]: event.target.value,
    });
  };

	//edit deck handling
	const [editState, setEditState] = useState({
    decks: { name: "", description: "" },
    placeholderName: "...loading",
    placeholderDescription: "...loading",
    editedDeck: {
      id: deckId,
      name: "",
      description: "",
    },
  });




  const { url } = useRouteMatch();
  if (url.includes("edit")) {
    return (
      <>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            placeholder={editState.placeholderName}
            onChange={handleNameChange}
            value={editState.editedDeck.name}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">
            Description
          </label>
          <textarea
            className="form-control"
            id="description"
            rows="3"
            placeholder={editState.placeholderDescription}
            onChange={handleDescriptionChange}
            value={editState.editedDeck.description}
          ></textarea>
          <button
            className="btn btn-secondary mr-2 mt-2"
            id="cancelEditDeck"
            onClick={OnCancel}
          >
            Cancel
          </button>
          <button
            className="btn btn-primary mt-2"
            id="submitEditDeck"
            onClick={onSubmit}
          >
            Submit
          </button>
        </div>
      </>
    );
  }

  return (
    <form>
      <div className="mb-3">
        <label htmlFor="deck-name" className="form-label">
          Name
        </label>
        <input
          className="form-control"
          id="name"
          type="text"
          name="name"
          onChange={handleChange}
          value={newDeckObject.name}
          placeholder="Deck Name"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="deck-description" className="form-label">
          Description
        </label>
        <textarea
          className="form-control"
          id="description"
          type="password"
          name="description"
          onChange={handleChange}
          value={newDeckObject.description}
          placeholder="Brief description of the deck"
        />
      </div>
      <button
        type="button"
        className="btn btn-secondary mr-2"
        id="cancelCreateDeck"
        onClick={OnClick}
      >
        Cancel
      </button>
      <button
        type="button"
        className="btn btn-primary"
        id="submitCreateDeck"
        onClick={OnClick}
      >
        Submit
      </button>
    </form>
  );
}

export default DeckForm;
