import React, { useState, useEffect, Fragment } from "react";
import NewDeck from "./NewDeck";

function DeckList({ OnClick, setAllDecks, allDecks }) {
  const deckList = [<NewDeck />];

  useEffect(() => {
    async function loadAllDecks() {
      const response = await fetch("http://localhost:5000/decks");
      const allDecksFromAPI = await response.json();
      setAllDecks(allDecksFromAPI);
    }
    loadAllDecks();
  }, []);

  console.log(allDecks);

  const listAllDecks = (
    <Fragment>
      <li className="list-group-item">
        <div className="card border-0">
          <div className="card-body">
            <h5 className="card-title">Rendering in React</h5>
            <p>3 cards</p>
            <p className="card-text">
              React's component structure allows for quickly building a complex
              web application that relies on DOM manipulation.
            </p>

            <button
              to="/"
              className="btn btn-secondary"
              id="viewDeck"
              onClick={OnClick}
            >
              View
            </button>
            <button
              to="/"
              className="btn btn-primary"
              id="studyDeck"
              onClick={OnClick}
            >
              Study
            </button>
            <button
              to="/"
              className="btn btn-danger"
              id="deleteDeck"
              onClick={OnClick}
            >
              Delete
            </button>
          </div>
        </div>
      </li>
    </Fragment>
  );

  return (
    <Fragment>
      <ul className="list-group">{listAllDecks}</ul>
    </Fragment>
  );
}

export default DeckList;
