import React, { Fragment, useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";

function Study({
  OnClick,
  currentDeck,
  setCurrentDeck,
  deckLength,
  setDeckLength,
}) {
  const params = useParams();
  const history = useHistory();
  const deckId = params.deckId;

  const [flipped, setFlipped] = useState(false);
  const [currentCard, setCurrentCard] = useState(1);
  let cardFront = "front text";
  let cardBack = "back text";

  //sets current deck with it's cards
  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${deckId}?_embed=cards`
      );
      const deckFromAPI = await response.json();
      setCurrentDeck(deckFromAPI);
    }
    loadDeck();
    //}, [deckId, currentDeck]); //this is right but makes computer slow while running server
  }, [deckId]);

  //sets current deck length
  useEffect(() => {
    async function loadLength() {
      const response = await fetch(
        `http://localhost:5000/deck/${deckId}/cards`
      );
      const lengthFromAPI = await response.json();
      setDeckLength(lengthFromAPI.length);
    }
    loadLength();
  }, [deckId]);
  // check dependency

  const cards = currentDeck.cards;

  if (cards) {
    for (let i = 0; i < cards.length; i++) {
      console.log(cards[i].front);
      if (currentCard = 1) {
				cardFront = cards[i]
			}

    }
  }

  console.log(currentCard);

  const onFlip = () => {
    setFlipped((prevState) => !prevState);
  };

  const onNext = () => {
    if (currentCard === deckLength) {
      const restart = window.confirm(
        "Restart cards? \n \n Click 'cancel' to return to the home page."
      );
      if (restart === true) {
        setFlipped(false);
        setCurrentCard(1);
      } else {
        history.push("/");
      }
    } else {
      setCurrentCard((prevValue) => prevValue + 1);
      setFlipped(false);
    }
  };

  const flipBtn = (
    <button onClick={onFlip} className="btn btn-secondary">
      Flip
    </button>
  );

  const bothBtns = (
    <Fragment>
      <button onClick={onFlip} className="btn btn-secondary">
        Flip
      </button>
      <button onClick={onNext} className="btn btn-primary">
        Flip
      </button>
    </Fragment>
  );

  const card = (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">
          Card {currentCard} of {deckLength}
        </h5>
        <p className="card-text">{flipped ? cardBack : cardFront}</p>
        {flipped ? bothBtns : flipBtn}
      </div>
    </div>
  );

  const notEnoughCards = (
    <Fragment>
      <h2>Not enough cards.</h2>
      <p>
        You need at least 3 cards to study. There{" "}
        {deckLength === 1 ? `is ${deckLength} card` : `are ${deckLength} cards`}{" "}
        in this deck.
      </p>
      <button className="btn btn-primary" id="studyAddCard" onClick={OnClick}>
        Add Cards
      </button>
    </Fragment>
  );

  return (
    <Fragment>
      <h1>Study: Deck Title</h1>
      {deckLength < 3 ? notEnoughCards : card}
    </Fragment>
  );
}

export default Study;
